import React, { useState } from "react";
import { NavLink, Route } from "react-router-dom";
import "./App.css";
import WheelPage from "./Components/WheelPage";

function App() {
  const [mail, setMail] = useState("");
  const [save, onSave] = useState("");

  function inputHandler(e) {
    setMail(e.target.value);
  }

  function saveMail() {
    if (mail.length === 0) {
      return alert("Please Enter Email");
    } else if (!mail.includes("@")) {
      return alert("Email is not valid");
    } else if (!mail.includes(".com")) {
      return alert("Email is not valid");
    }
    onSave((prevMail) => (prevMail = mail));
  }

  console.log(save);

  return (
    <div>
      <Route path="/game">
        <WheelPage />
      </Route>
      <div>
        <div className="top">
          <img
            src="https://influencex-profile-pictures.nyc3.cdn.digitaloceanspaces.com/themes/CarnivalTop.png"
            alt="wheel"
          />
        </div>
        <div className="bottom">
          <img
            src="https://influencex-profile-pictures.nyc3.cdn.digitaloceanspaces.com/themes/CarnivalBottom.webp"
            alt=""
          />
        </div>
        <div className="app-container">
          <div>
            <img
              className="wheel"
              src="https://games.engagebud.com/static/media/wheelimage.67aed50ccb8573880d27.webp"
              alt="wheel-img"
            />
          </div>
          <div className="info">
            <h1>Spin the wheel for a Surprise offer</h1>
            <input
              onChange={inputHandler}
              type="text"
              placeholder="Your Email Here"
            />
            <div className="progress">
              <div className="fill a"></div>
            </div>
            <p>80% offers claimed. Hurry Up!</p>
            {save && (
              <NavLink to="/game">
                <button onClick={saveMail}>Try your luck</button>
              </NavLink>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
